                ax.annotate(
                    f"{height:.2f}%",
                    xy=(bar.get_x() + bar.get_width() / 2, height),
                    xytext=(0, vertical_offset),  # Ajuste dinâmico com base na altura da barra
                    textcoords="offset points",
                    ha="center",
                    va="top" if height >= 0 else "bottom",  # Alinhamento vertical ajustado para dentro da barra
                )

            # Linha horizontal no y=0
            ax.axhline(0, color="gray", linewidth=0.8)

            # Rotação dos rótulos no eixo x para evitar sobreposição
            plt.xticks(rotation=45)
            return fig

