    width: calc(0.7071 * var(--hex-width));
    height: calc(0.7071 * var(--hex-width));
    background-color: inherit;
    transform-origin: 0 0;
    transform: scaleY(0.57735) rotate(-45deg);
    animation: inherit;
  }
  .loading-animation .hex::before {
