    -webkit-animation-delay: -1.6s;
    animation-delay: -1.6s;
  }
  .loading-animation .hex:nth-of-type(2n + 12) {
    -webkit-animation-delay: -1.6s;
    animation-delay: -1.6s;
  }
  .loading-animation .hex:nth-of-type(2n + 13) {
    -webkit-animation-delay: -1.3s;
    animation-delay: -1.3s;
